let mouseX = 0;
let mouseY = 0;

document.addEventListener("mousemove", e => {
  mouseX = e.clientX;
  mouseY = e.clientY;
});

document.addEventListener("click", e => {
  if(dws!=null){
    const payLoad = {
      m: "m",
      x: mouseX,
      y: mouseY,
      w: document.body.scrollWidth,
      h: document.body.scrollHeight
    };
    dws.send(msgpack.encode(payLoad));
  }
});

